function startSlide() {
  setInterval(function () {
    (document.getElementById('radio' + counter).checked = !0),
      counter++,
      counter > 3 && (counter = 1);
  }, 5e3);
}
document.addEventListener('DOMContentLoaded', function (e) {
  let t =
      null === window.localStorage.getItem('mode')
        ? 'light'
        : window.localStorage.getItem('mode'),
    o =
      null === window.localStorage.getItem('lang')
        ? 'SL'
        : window.localStorage.getItem('lang');
  document.querySelectorAll('.slides input').forEach(function (e) {
    e.onclick = e => {
      counter = e.target.id.slice(-1)[0];
    };
  });
  const n = document.documentElement,
    c = document.getElementById('mode-toggle1'),
    l = document.getElementById('mode-toggle2'),
    r = document.querySelector('#mode-toggle1 ~ label'),
    a = document.querySelector('#mode-toggle2 ~ label');
  (c.checked = l.checked = 'dark' === t),
    n.style.setProperty('--primary-color', c.checked ? 'black' : 'white'),
    n.style.setProperty(
      '--primary-contrast-color',
      c.checked ? 'white' : 'black'
    ),
    (r.innerText = a.innerText = c.checked ? 'Light' : 'Dark'),
    console.log({ mode: t, modeToggle: c.checked, modeLabel: r.innerText }),
    (c.onclick = () => {
      c.checked
        ? (n.style.setProperty('--primary-color', 'black'),
          n.style.setProperty('--primary-contrast-color', 'white'),
          (r.innerText = 'Light'),
          (a.innerText = 'Light'),
          window.localStorage.setItem('mode', 'dark'))
        : (n.style.setProperty('--primary-color', 'white'),
          n.style.setProperty('--primary-contrast-color', 'black'),
          (r.innerText = 'Dark'),
          (a.innerText = 'Dark'),
          window.localStorage.setItem('mode', 'light')),
        (l.checked = c.checked);
    }),
    (l.onclick = () => {
      l.checked
        ? (n.style.setProperty('--primary-color', 'black'),
          n.style.setProperty('--primary-contrast-color', 'white'),
          (a.innerText = 'Light'),
          (r.innerText = 'Light'),
          window.localStorage.setItem('mode', 'dark'))
        : (n.style.setProperty('--primary-color', 'white'),
          n.style.setProperty('--primary-contrast-color', 'black'),
          (a.innerText = 'Dark'),
          (r.innerText = 'Dark'),
          window.localStorage.setItem('mode', 'light')),
        (c.checked = l.checked);
    });
  const d = document.getElementById('lang-toggle1'),
    i = document.getElementById('lang-toggle2');
  d.checked = i.checked = 'EN' === o;
  let g = document.querySelector('#lang-toggle1  ~ label'),
    m = document.querySelector('#lang-toggle2  ~ label');
  (g.innerText = m.innerText = d.checked ? 'SL' : 'EN'),
    console.log({ lang: o, langToggle: d.checked, langLabel: g.innerText }),
    (d.onclick = () => {
      d.checked
        ? ((g.innerText = 'SL'),
          (m.innerText = 'SL'),
          window.localStorage.setItem('lang', 'EN'))
        : ((g.innerText = 'EN'),
          (m.innerText = 'EN'),
          window.localStorage.setItem('lang', 'SL')),
        (i.checked = d.checked);
    }),
    (i.onclick = () => {
      i.checked
        ? ((m.innerText = 'SL'),
          (g.innerText = 'SL'),
          window.localStorage.setItem('lang', 'EN'))
        : ((m.innerText = 'EN'),
          (g.innerText = 'EN'),
          window.localStorage.setItem('lang', 'SL')),
        (d.checked = i.checked);
    }),
    startSlide();
});
